import{_ as a,o as s,c as e,b as l}from"./app.fa26c539.js";const A=JSON.parse('{"title":"Lab S","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u52D8\u8BEF","slug":"\u52D8\u8BEF"},{"level":2,"title":"handout","slug":"handout"},{"level":2,"title":"\u63D0\u4EA4\u8BF4\u660E","slug":"\u63D0\u4EA4\u8BF4\u660E"},{"level":2,"title":"DDL","slug":"ddl"},{"level":2,"title":"Boost \u5E93","slug":"boost-\u5E93"},{"level":3,"title":"\u9644\uFF1AAPT","slug":"\u9644\uFF1Aapt"},{"level":2,"title":"CMake","slug":"cmake"}],"relativePath":"labs/labS.md","lastUpdated":1671289981000}'),n={name:"labs/labS.md"},o=l(`<h1 id="lab-s" tabindex="-1">Lab S <a class="header-anchor" href="#lab-s" aria-hidden="true">#</a></h1><h2 id="\u52D8\u8BEF" tabindex="-1">\u52D8\u8BEF <a class="header-anchor" href="#\u52D8\u8BEF" aria-hidden="true">#</a></h2><p><code>include/common.h</code> \u4E2D\u9700\u8981\u989D\u5916\u5305\u542B\u5934\u6587\u4EF6\uFF1A<code>#include &lt;bitset&gt;</code>\u3002</p><h2 id="handout" tabindex="-1">handout <a class="header-anchor" href="#handout" aria-hidden="true">#</a></h2><ul><li><a href="/pdf/LAB_S.pdf">PDF</a></li><li><a href="/LAB_S_Attachment.zip">\u6846\u67B6</a></li><li><a href="/LAB_S_Test.zip">\u6D4B\u8BD5\u7528\u4F8B</a></li></ul><p><a href="/resource/cpp.html">\u53EF\u80FD\u9700\u8981\u7684 C++ \u77E5\u8BC6</a></p><ul><li>\u8981\u6C42\u5F97\u5230\u80FD\u6B63\u786E\u6267\u884C\u7684\u6A21\u62DF\u5668\uFF0C\u4E0D\u4E25\u683C\u8981\u6C42\u4F7F\u7528\u6846\u67B6\u4EE3\u7801/\u6846\u67B6\u63D0\u4F9B\u7684\u6784\u5EFA\u65B9\u5F0F\uFF0C\u4F46\u8981\u6C42\u4F7F\u7528 C/C++ \u8BED\u8A00\u5B8C\u6210\u3002</li></ul><h2 id="\u63D0\u4EA4\u8BF4\u660E" tabindex="-1">\u63D0\u4EA4\u8BF4\u660E <a class="header-anchor" href="#\u63D0\u4EA4\u8BF4\u660E" aria-hidden="true">#</a></h2><ul><li>\u5B9E\u9A8C\u5B8C\u6210\u540E\u817E\u8BAF\u4F1A\u8BAE\u7EBF\u4E0A\u63D0\u95EE\u68C0\u67E5\uFF0C\u65F6\u95F4\u4E3A\u6BCF\u5929\u4E0A\u5348 9:30 \u5230 10:30 <ul><li>\u6BCF\u5929\u68C0\u67E5\u524D\u4F1A\u5728\u7FA4\u5185\u516C\u5E03\u817E\u8BAF\u4F1A\u8BAE\u53F7</li></ul></li><li>BB \u4E0A\u9700\u8981\u63D0\u4EA4 report (5% \u5206\u6570) <ul><li>\u6CA1\u6709\u683C\u5F0F\u8981\u6C42\uFF0C\u7B80\u7565\u8BF4\u660E\u5373\u53EF</li></ul></li></ul><h2 id="ddl" tabindex="-1">DDL <a class="header-anchor" href="#ddl" aria-hidden="true">#</a></h2><p>\u68C0\u67E5\u5B9E\u9A8C\u65F6\u95F4\u6700\u665A 2023 \u5E74 1 \u6708 7 \u65E5\u4E0A\u5348 10:30\uFF0C\u4E4B\u540E\u4E0D\u63A5\u53D7\u68C0\u67E5 or \u63D0\u4EA4\u3002</p><h2 id="boost-\u5E93" tabindex="-1">Boost \u5E93 <a class="header-anchor" href="#boost-\u5E93" aria-hidden="true">#</a></h2><p>\u5728 labA \u7684\u4ECB\u7ECD\u4E2D\uFF0C\u6211\u4EEC\u63D0\u5230 Linux \u4E0B\u53EF\u4EE5\u5F88\u65B9\u4FBF\u7684\u4F7F\u7528\u5305\u7BA1\u7406\u5668\u3002\u672C\u5B9E\u9A8C\u5C31\u5C06\u4F53\u73B0\u8FD9\u4E00\u70B9\u3002</p><blockquote><p>Boost \u5E93\u662F\u4E3A C++ \u8BED\u8A00\u6807\u51C6\u5E93\u63D0\u4F9B\u6269\u5C55\u7684\u4E00\u4E9B C++ \u7A0B\u5E8F\u5E93\u7684\u603B\u79F0\u3002Boost \u5E93\u7531 Boost \u793E\u533A\u7EC4\u7EC7\u5F00\u53D1\u3001\u7EF4\u62A4\u3002\u5176\u76EE\u7684\u662F\u4E3A C++ \u7A0B\u5E8F\u5458\u63D0\u4F9B\u514D\u8D39\u3001\u540C\u884C\u5BA1\u67E5\u7684\u3001\u53EF\u79FB\u690D\u7684\u7A0B\u5E8F\u5E93\u3002Boost \u5E93\u53EF\u4EE5\u4E0E C++ \u6807\u51C6\u5E93\u5B8C\u7F8E\u5171\u540C\u5DE5\u4F5C\uFF0C\u5E76\u4E14\u4E3A\u5176\u63D0\u4F9B\u6269\u5C55\u529F\u80FD\u3002Boost \u5E93\u4F7F\u7528 BoostLicense \u6765\u6388\u6743\u4F7F\u7528\uFF0C\u6839\u636E\u8BE5\u534F\u8BAE\uFF0C\u5546\u4E1A\u7684\u975E\u5546\u4E1A\u7684\u4F7F\u7528\u90FD\u662F\u5141\u8BB8\u5E76\u9F13\u52B1\u7684\u3002</p></blockquote><p>Boost \u5E93\u4E00\u822C\u4EE5\u6E90\u6587\u4EF6\uFF08\u5934\u6587\u4EF6\uFF0C\u4FBF\u4E8E\u77E5\u9053\u51FD\u6570\u58F0\u660E\uFF09\u548C\u94FE\u63A5\u5E93\u5F62\u5F0F\u5728\u5305\u7BA1\u7406\u5668\u5206\u53D1\u3002\u5BF9\u4E8E Ubuntu \u7CFB\u7EDF\u800C\u8A00\uFF1A</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">sudo apt update</span></span>
<span class="line"><span style="color:#A6ACCD;">sudo apt install libboost-all-dev</span></span>
<span class="line"></span></code></pre></div><p>\u5373\u53EF\u5B89\u88C5\u4E0A Boost \u5E93\u3002</p><p>\u53EF\u4EE5\u5C1D\u8BD5\u4F7F\u7528 <code>find /usr -name boost</code> \u6765\u627E\u5230\u5B89\u88C5\u540E\u7684 <code>boost</code> \u5E93\u7684\u4F4D\u7F6E\u3002</p><h3 id="\u9644\uFF1Aapt" tabindex="-1">\u9644\uFF1AAPT <a class="header-anchor" href="#\u9644\uFF1Aapt" aria-hidden="true">#</a></h3><blockquote><p>APT \u7684\u5168\u79F0\u662F Advance Package Tool\uFF0C\u662F\u4E00\u4E2A\u5904\u7406\u5728 Debian\u3001Ubuntu \u6216\u8005\u5176\u4ED6\u884D\u751F\u53D1\u884C\u7248\u7684 Linux \u4E0A\u5B89\u88C5\u548C\u79FB\u9664\u8F6F\u4EF6\u7684\u81EA\u7531\u8F6F\u4EF6\u3002</p><p>\u4E00\u822C\u6211\u4EEC\u6240\u8BA4\u77E5\u7684\u300C\u8F6F\u4EF6\u5546\u5E97\u300D\u90FD\u662F\u5168\u7A0B\u8054\u7F51\u7684\uFF0C\u641C\u7D22\u7B49\u64CD\u4F5C\u90FD\u4F1A\u5F80\u670D\u52A1\u5668\u53D1\u9001\u8BF7\u6C42\u540E\u8FD4\u56DE\u6700\u65B0\u7684\u7ED3\u679C\uFF0C\u4F46 APT \u7684\u7B56\u7565\u6709\u6240\u4E0D\u540C\uFF1A\u5728\u8BA1\u7B97\u673A\u672C\u5730\uFF0C\u7CFB\u7EDF\u4F1A\u7EF4\u62A4\u4E00\u4E2A\u5305\u5217\u8868\uFF0C\u5728\u8FD9\u4E2A\u5217\u8868\u91CC\u9762\uFF0C\u5305\u542B\u4E86\u8F6F\u4EF6\u4FE1\u606F\u4EE5\u53CA\u8F6F\u4EF6\u5305\u7684\u4F9D\u8D56\u5173\u7CFB\uFF0C\u5728\u6267\u884C <code>apt install</code> \u547D\u4EE4\u65F6\uFF0C\u4F1A\u4ECE\u8FD9\u4E2A\u5217\u8868\u4E2D\u8BFB\u53D6\u51FA\u60F3\u8981\u5B89\u88C5\u7684\u8F6F\u4EF6\u4FE1\u606F\uFF0C\u5305\u62EC\u4E0B\u8F7D\u5730\u5740\u3001\u8F6F\u4EF6\u7248\u672C\u3001\u4F9D\u8D56\u7684\u5305\uFF0C\u540C\u65F6 apt \u4F1A\u5BF9\u4F9D\u8D56\u7684\u5305\u9012\u5F52\u6267\u884C\u5982\u4E0A\u64CD\u4F5C\uFF0C\u76F4\u5230\u4E0D\u518D\u6709\u65B0\u7684\u4F9D\u8D56\u5305\u3002\u5982\u4E0A\u5F97\u5230\u7684\u6240\u6709\u5305\uFF0C\u5C06\u4F1A\u662F\u5728 <code>apt install some-package</code> \u65F6\u5B89\u88C5\u7684\u3002</p><p>\u672C\u5730\u7684\u8FD9\u4E00\u5305\u5217\u8868\u4E00\u822C\u9700\u8981\u901A\u8FC7 <code>apt update</code> \u547D\u4EE4\u624B\u52A8\u66F4\u65B0\u3002</p></blockquote><p>\u4EE5\u4E0A\u5185\u5BB9\u53C2\u8003\u81EA <a href="https://101.lug.ustc.edu.cn/Ch03/#use-package-management-system" target="_blank" rel="noreferrer">Linux 101-\u4F7F\u7528\u5305\u7BA1\u7CFB\u7EDF\u5B89\u88C5</a>\uFF08<a href="https://creativecommons.org/licenses/by-sa/4.0/" target="_blank" rel="noreferrer">CC BY-SA 4.0 license</a>\uFF09\u3002</p><h2 id="cmake" tabindex="-1">CMake <a class="header-anchor" href="#cmake" aria-hidden="true">#</a></h2><p>\u4E4B\u524D\u6211\u4EEC\u5DF2\u7ECF\u4ECB\u7ECD\u4E86 Makefile\uFF0C\u90A3\u4E48 CMake \u53C8\u6709\u4EC0\u4E48\u7528\u5462\uFF1F</p><ul><li>CMake \u7684\u8BED\u6CD5\u66F4\u52A0\u7B80\u5355</li><li>CMake \u80FD\u66F4\u597D\u7684\u8DE8\u5E73\u53F0\uFF0C\u4E0D\u4EC5\u53EF\u4EE5\u751F\u6210 Makefile\uFF0C\u8FD8\u53EF\u4EE5\u517C\u5BB9\u5176\u4ED6\u6784\u5EFA\u7CFB\u7EDF</li></ul><p>\u4F8B\u5982\uFF0C\u5982\u679C\u4F60\u60F3\u5728\u4F60\u7684\u9879\u76EE\u4E2D\u4F7F\u7528\u4F60\u5B89\u88C5\u597D\u7684 Boost \u5E93\uFF0C\u4F7F\u7528 Make \u53EF\u80FD\u9700\u8981\u624B\u52A8\u6307\u5B9A Boost \u5E93\u7684\u8DEF\u5F84\uFF0C\u4F46\u662F CMake \u53EF\u4EE5\u81EA\u52A8\u5BFB\u627E Boost \u5E93\uFF0C\u6240\u4EE5\u53EA\u9700\u8981\u6709\u4E0B\u9762\u51E0\u884C CMake \u4EE3\u7801\u5C31\u53EF\u4EE5\u4E86\uFF1A</p><div class="language-cmake"><button class="copy"></button><span class="lang">cmake</span><pre><code><span class="line"><span style="color:#A6ACCD;">......</span></span>
<span class="line"><span style="color:#89DDFF;">set</span><span style="color:#A6ACCD;">(Boost_USE_STATIC_LIBS </span><span style="color:#89DDFF;">OFF</span><span style="color:#A6ACCD;">)</span></span>
<span class="line"><span style="color:#89DDFF;">set</span><span style="color:#A6ACCD;">(Boost_USE_MULTITHREADED </span><span style="color:#89DDFF;">ON</span><span style="color:#A6ACCD;">)</span></span>
<span class="line"><span style="color:#89DDFF;">set</span><span style="color:#A6ACCD;">(Boost_USE_STATIC_RUNTIME </span><span style="color:#89DDFF;">OFF</span><span style="color:#A6ACCD;">)</span></span>
<span class="line"><span style="color:#89DDFF;">find_package</span><span style="color:#A6ACCD;">(Boost 1.45.0 COMPONENTS program_options)</span></span>
<span class="line"><span style="color:#A6ACCD;">......</span></span>
<span class="line"><span style="color:#89DDFF;">if</span><span style="color:#A6ACCD;">(Boost_FOUND)</span></span>
<span class="line"><span style="color:#89DDFF;">    include_directories</span><span style="color:#A6ACCD;">(\${Boost_INCLUDE_DIRS})</span></span>
<span class="line"><span style="color:#89DDFF;">    target_link_libraries</span><span style="color:#A6ACCD;">(lc3simulator \${Boost_LIBRARIES})</span></span>
<span class="line"><span style="color:#89DDFF;">endif</span><span style="color:#A6ACCD;">()</span></span>
<span class="line"></span></code></pre></div><p>\u5F53\u7136\uFF0C\u672C\u6B21\u5B9E\u9A8C\u6211\u4EEC\u5E76\u4E0D\u9700\u8981\u77E5\u9053\u5177\u4F53\u7684 CMake \u8BED\u6CD5\uFF0C\u53EA\u9700\u8981\u4F1A\u4F7F\u7528\u5C31\u53EF\u4EE5\u4E86\u3002</p><div class="language-bash"><button class="copy"></button><span class="lang">bash</span><pre><code><span class="line"><span style="color:#A6ACCD;">mkdir build </span><span style="color:#676E95;"># \u521B\u5EFA\u4E00\u4E2A\u6587\u4EF6\u5939\uFF0C\u540D\u5B57\u53EB build\uFF0C\u6211\u4EEC\u5728\u8FD9\u91CC\u751F\u6210\u53EF\u6267\u884C\u6587\u4EF6</span></span>
<span class="line"><span style="color:#82AAFF;">cd</span><span style="color:#A6ACCD;"> build    </span><span style="color:#676E95;"># \u8FDB\u5165 build \u6587\u4EF6\u5939</span></span>
<span class="line"><span style="color:#A6ACCD;">cmake ..    </span><span style="color:#676E95;"># \u4E24\u4E2A\u70B9\u662F\u4E0A\u4E00\u7EA7\u76EE\u5F55\u7684\u610F\u601D\uFF0C\u8FD9\u91CC\u662F\u6839\u636E\u4E0A\u4E00\u7EA7\u76EE\u5F55\uFF08\u4E5F\u5C31\u662F\u9879\u76EE\u6839\u76EE\u5F55\uFF09\u7684 CMake \u914D\u7F6E\u6587\u4EF6\u6765\u4EA7\u751F Makefile</span></span>
<span class="line"><span style="color:#A6ACCD;">make        </span><span style="color:#676E95;"># \u6700\u540E\u6784\u5EFA\u51FA\u53EF\u6267\u884C\u6587\u4EF6 lc3simulator</span></span>
<span class="line"></span></code></pre></div>`,28),t=[o];function p(c,r,i,d,u,h){return s(),e("div",null,t)}const D=a(n,[["render",p]]);export{A as __pageData,D as default};
